CREATE TABLE IF NOT EXISTS `alert_settings` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `company_id` bigint unsigned NOT NULL,
  `name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `slug` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `low_stock_threshold_days` int NOT NULL DEFAULT '30' COMMENT 'Days of stock remaining to trigger low stock alert',
  `critical_stock_percentage` decimal(5,2) NOT NULL DEFAULT '25.00' COMMENT 'Percentage of minimum stock for critical priority',
  `high_stock_percentage` decimal(5,2) NOT NULL DEFAULT '50.00' COMMENT 'Percentage of minimum stock for high priority',
  `medium_stock_percentage` decimal(5,2) NOT NULL DEFAULT '75.00' COMMENT 'Percentage of minimum stock for medium priority',
  `expiring_soon_days` int NOT NULL DEFAULT '30' COMMENT 'Days before expiration to trigger alert',
  `expiring_critical_days` int NOT NULL DEFAULT '7' COMMENT 'Days for critical expiration alert',
  `expiring_high_days` int NOT NULL DEFAULT '15' COMMENT 'Days for high priority expiration alert',
  `email_alerts_enabled` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'Enable/disable email alerts',
  `email_recipients` json DEFAULT NULL COMMENT 'Array of email addresses to receive alerts',
  `email_on_critical_only` tinyint(1) NOT NULL DEFAULT '1' COMMENT 'Send emails only for critical alerts',
  `email_on_low_stock` tinyint(1) NOT NULL DEFAULT '1' COMMENT 'Send emails for low stock alerts',
  `email_on_out_of_stock` tinyint(1) NOT NULL DEFAULT '1' COMMENT 'Send emails for out of stock alerts',
  `email_on_expiring` tinyint(1) NOT NULL DEFAULT '1' COMMENT 'Send emails for expiring products',
  `email_on_expired` tinyint(1) NOT NULL DEFAULT '1' COMMENT 'Send emails for expired products',
  `email_frequency` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'immediate' COMMENT 'immediate, daily_digest, weekly_digest',
  `digest_time` time DEFAULT NULL COMMENT 'Time to send digest emails',
  `browser_notifications_enabled` tinyint(1) NOT NULL DEFAULT '1',
  `dashboard_alerts_enabled` tinyint(1) NOT NULL DEFAULT '1',
  `is_active` tinyint(1) NOT NULL DEFAULT '1',
  `active_at` timestamp NULL DEFAULT NULL,
  `created_by` bigint unsigned DEFAULT NULL,
  `updated_by` bigint unsigned DEFAULT NULL,
  `deleted_by` bigint unsigned DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `alert_settings_slug_unique` (`slug`),
  KEY `alert_settings_created_by_foreign` (`created_by`),
  KEY `alert_settings_updated_by_foreign` (`updated_by`),
  KEY `alert_settings_deleted_by_foreign` (`deleted_by`),
  KEY `alert_settings_company_id_is_active_index` (`company_id`,`is_active`),
  KEY `alert_settings_slug_index` (`slug`),
  CONSTRAINT `alert_settings_company_id_foreign` FOREIGN KEY (`company_id`) REFERENCES `companies` (`id`) ON DELETE CASCADE,
  CONSTRAINT `alert_settings_created_by_foreign` FOREIGN KEY (`created_by`) REFERENCES `users` (`id`) ON DELETE SET NULL,
  CONSTRAINT `alert_settings_deleted_by_foreign` FOREIGN KEY (`deleted_by`) REFERENCES `users` (`id`) ON DELETE SET NULL,
  CONSTRAINT `alert_settings_updated_by_foreign` FOREIGN KEY (`updated_by`) REFERENCES `users` (`id`) ON DELETE SET NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


